// src/Berichten/ChatPage.js
import React from 'react';
import { useParams } from 'react-router-dom';
import { messages } from './messages'; // Import updated messages

function ChatPage() {
  const { messageId } = useParams(); // Retrieve the message ID from the URL

  // Filter messages based on the current contact conversation (sender/receiver)
  const contactMessages = messages.filter(
    (msg) =>
      (msg.sender === 'You' && msg.receiver === messageId) ||
      (msg.receiver === 'You' && msg.sender === messageId)
  );

  // Sort messages by timestamp to display them in order
  const sortedMessages = contactMessages.sort((a, b) => new Date(a.timestamp) - new Date(b.timestamp));

  return (
    <div>
      <h2>Chat with {messageId}</h2>
      <div>
        {sortedMessages.map((msg) => (
          <div
            key={msg.id}
            style={{
              display: 'flex',
              marginBottom: '10px',
              justifyContent: msg.sender === 'You' ? 'flex-end' : 'flex-start',
            }}
          >
            <img
              src={msg.profilePicture}
              alt={`${msg.sender}'s profile`}
              style={{
                width: '50px',
                height: '50px',
                borderRadius: '50%',
                marginLeft: msg.sender === 'You' ? '0' : '10px',
                marginRight: msg.sender === 'You' ? '10px' : '0',
              }}
            />
            <div
              style={{
                backgroundColor: msg.sender === 'You' ? '#007bff' : '#f1f1f1',
                color: msg.sender === 'You' ? 'white' : 'black',
                padding: '10px',
                borderRadius: '10px',
                maxWidth: '70%',
                wordWrap: 'break-word',
              }}
            >
              <p>{msg.message}</p>
              <small>{new Date(msg.timestamp).toLocaleString()}</small>
            </div>
          </div>
        ))}
      </div>
    </div>
  );
}

export default ChatPage;
