import React, { useState } from 'react';
import { useParams } from 'react-router-dom';
import { messages as initialMessages } from './messages'; 
import { Input, Button, Row, Col } from 'reactstrap';

function ChatPage() {
  const { messageId } = useParams(); 
  const [messages, setMessages] = useState(initialMessages); 
  const [newMessage, setNewMessage] = useState('');

  const contactMessages = messages.filter(
    (msg) =>
      (msg.sender === messageId && msg.receiver === 'You') ||
      (msg.receiver === messageId && msg.sender === 'You')
  );

  const sortedMessages = contactMessages.sort((a, b) => new Date(a.timestamp) - new Date(b.timestamp));

  const handleSendMessage = () => {
    if (newMessage.trim() === '') return;

    const nieuwBericht = {
      id: messages.length + 1,
      sender: 'You',
      receiver: messageId,
      message: newMessage,
      timestamp: new Date().toISOString(),
      profilePicture: '/path-to-your-profile-picture.jpg',
    };

    setMessages((prevMessages) => [...prevMessages, nieuwBericht]);
    setNewMessage('');
  };

  return (
    <div>
      <h4 className="mb-4">Chat with {messageId}</h4>
      <div className="chat-container mb-4" style={{ maxHeight: '70vh', overflowY: 'auto' }}>
        {sortedMessages.map((msg) => (
          <Row
            key={msg.id}
            className={`mb-3 ${msg.sender === 'You' ? 'justify-content-end' : 'justify-content-start'}`}
          >
            {msg.sender !== 'You' && (
              <img
                src={msg.profilePicture}
                alt={`${msg.sender}'s profile`}
                className="rounded-circle me-2"
                style={{ width: '40px', height: '40px' }}
              />
            )}
            <Col xs="auto" className={`p-2 ${msg.sender === 'You' ? 'bg-primary text-white' : 'bg-light text-dark'}`} style={{ borderRadius: '10px' }}>
              <p className="mb-1">{msg.message}</p>
              <small className="text-muted">{new Date(msg.timestamp).toLocaleString()}</small>
            </Col>
          </Row>
        ))}
      </div>

      <div className="d-flex">
        <Input
          type="text"
          value={newMessage}
          onChange={(e) => setNewMessage(e.target.value)}
          placeholder="Type your message"
          className="me-2"
        />
        <Button color="primary" onClick={handleSendMessage}>
          Send
        </Button>
      </div>
    </div>
  );
}

export default ChatPage;
