// src/Berichten/berichten.js
import React, { useState, useEffect } from 'react';
import { Link, Outlet } from 'react-router-dom';
import { messages } from './messages'; // Import messages data

function BerichtenPagina() {
  const [messagesData, setMessagesData] = useState([]);

  useEffect(() => {
    setMessagesData(messages); // Set messages state with data from the JSON file
  }, []);

  // Group the messages by sender and receiver
  const groupMessages = (messages) => {
    const grouped = messages.reduce((acc, msg) => {
      const contactId = msg.receiver === 'You' ? msg.sender : msg.receiver;
      if (!acc[contactId]) {
        acc[contactId] = [];
      }
      acc[contactId].push(msg);
      return acc;
    }, {});

    return Object.entries(grouped).map(([contactId, msgs]) => ({
      contactId,
      messages: msgs,
      lastMessage: msgs[msgs.length - 1], // Get the last message in the conversation
    }));
  };

  const groupedMessages = groupMessages(messagesData);

  return (
    <div style={{ display: 'flex' }}>
      {/* Left Sidebar: Chat List */}
      <div style={{ width: '300px', padding: '20px', borderRight: '1px solid #ddd' }}>
        <h2>Chats</h2>
        {groupedMessages.map(({ contactId, messages, lastMessage }) => (
          <Link
            key={contactId}
            to={`/berichten/${contactId}`} // Use contactId for the chat URL
            style={{
              textDecoration: 'none',
              color: 'black',
              display: 'flex',
              marginBottom: '10px',
            }}
          >
            <img
              src={lastMessage.profilePicture}
              alt={`${contactId}'s profile`}
              style={{ width: '50px', height: '50px', borderRadius: '50%' }}
            />
            <div style={{ marginLeft: '10px' }}>
              <p>
                <strong>{contactId}</strong>
                <small>{lastMessage.timestamp}</small>
              </p>
              <p>{lastMessage.message}</p>
            </div>
          </Link>
        ))}
      </div>

      {/* Right Section: Displaying selected chat */}
      <div style={{ flex: 1, padding: '20px' }}>
        <h2>Chat Details</h2>
        <Outlet /> {/* This will render the individual chat details */}
      </div>
    </div>
  );
}

export default BerichtenPagina;
