import React, { useState, useEffect } from 'react';
import { Link, Outlet } from 'react-router-dom';
import { messages } from './messages';
import { Row, Col, ListGroup, ListGroupItem } from 'reactstrap';

function BerichtenPagina() {
  const [messagesData, setMessagesData] = useState([]);

  useEffect(() => {
    setMessagesData(messages);
  }, []);

  const lastMessages = messagesData.reduce((acc, msg) => {
    if (msg.sender === 'You') {
      return acc;
    }

    const contactId = msg.receiver === 'You' ? msg.sender : msg.receiver;

    if (!acc[contactId]) {
      acc[contactId] = msg;
    } else if (new Date(msg.timestamp) > new Date(acc[contactId].timestamp)) {
      acc[contactId] = msg;
    }
    return acc;
  }, {});

  const lastMessagesArray = Object.values(lastMessages);

  return (
    <Row className="h-100">
      {/* Chat List Sidebar */}
      <Col xs="3" className="border-end p-3" style={{ overflowY: 'auto' }}>
        <h4 className="mb-3">Chats</h4>
        <ListGroup flush>
          {lastMessagesArray.map((msg) => (
            <ListGroupItem
              key={msg.id}
              tag={Link}
              to={`/berichten/${msg.receiver === 'You' ? msg.sender : msg.receiver}`}
              className="d-flex align-items-start text-decoration-none text-dark"
            >
              <img
                src={msg.profilePicture}
                alt={`${msg.receiver === 'You' ? msg.sender : msg.receiver}'s profile`}
                className="rounded-circle me-3"
                style={{ width: '50px', height: '50px' }}
              />
              <div>
                <strong>{msg.receiver === 'You' ? msg.sender : msg.receiver}</strong>
                <p className="mb-0 text-muted" style={{ fontSize: 'small' }}>{msg.message}</p>
              </div>
            </ListGroupItem>
          ))}
        </ListGroup>
      </Col>

      {/* Selected Chat Section */}
      <Col xs="9" className="p-3">
        <Outlet />
      </Col>
    </Row>
  );
}

export default BerichtenPagina;
