import React from 'react';
import { BrowserRouter as Router, Routes, Route, useLocation } from 'react-router-dom';
import Login from './pages/Login/Login';
import Registreren from './pages/Register/Registreren';
import ProfielAanmaken from './pages/profiel-aanmaken/profiel-aanmaken';
import Home from './pages/Home/Home';
import Header from './components/Header/Header';
import UserProfile from './pages/UserProfile/UserProfile'; // Import the UserProfile component

const App = () => {
  const location = useLocation();
  const hideHeaderRoutes = ['/login', '/register']; // Routes where the header should be hidden

  return (
    <Router>
      {!hideHeaderRoutes.includes(location.pathname) && <Header />}
      <Routes>
        <Route path="/" element={<Login />} />
        <Route path="/login" element={<Login />} />
        <Route path="/register" element={<Registreren />} />
        <Route path="/profiel-aanmaken" element={<ProfielAanmaken />} />
        <Route path="/home" element={<Home />} />
        <Route path="/user-profile" element={<UserProfile />} /> {/* Add the UserProfile route */}
      </Routes>
    </Router>
  );
}

export default App;
