// src/Berichten/berichten.js
import React from "react";
import { Link } from "react-router-dom"; // Import Link for routing

function BerichtenPagina() {
  // Fake data for messages
  const messages = [
    {
      id: 1,
      name: "Alice",
      profilePicture: "https://via.placeholder.com/50",
      message: "Hey! How are you doing?",
      timestamp: "2025-01-21T10:30:00",
    },
    {
      id: 2,
      name: "Bob",
      profilePicture: "https://via.placeholder.com/50",
      message: "I'm doing great, thanks! How about you?",
      timestamp: "2025-01-21T10:32:00",
    },
    {
      id: 3,
      name: "Charlie",
      profilePicture: "https://via.placeholder.com/50",
      message: "Don't forget about the meeting later.",
      timestamp: "2025-01-21T10:35:00",
    },
    {
      id: 4,
      name: "Alice",
      profilePicture: "https://via.placeholder.com/50",
      message: "Thanks for the reminder! See you there.",
      timestamp: "2025-01-21T10:36:00",
    },
    {
      id: 5,
      name: "Bob",
      profilePicture: "https://via.placeholder.com/50",
      message: "Let’s grab lunch after the meeting?",
      timestamp: "2025-01-21T10:37:00",
    },
  ];

  return (
    <div>
      <h1>Berichten</h1>
      <div>
        {messages.map((msg) => (
          <Link
            key={msg.id}
            to={`/berichten/${msg.id}`}
            style={{ textDecoration: "none", color: "black" }}
          >
            <div style={{ display: "flex", marginBottom: "10px", cursor: "pointer" }}>
              <img
                src={msg.profilePicture}
                alt={`${msg.name}'s profile`}
                style={{ width: "50px", height: "50px", borderRadius: "50%" }}
              />
              <div style={{ marginLeft: "10px" }}>
                <p>
                  <strong>{msg.name}</strong> <small>{msg.timestamp}</small>
                </p>
                <p>{msg.message}</p>
              </div>
            </div>
          </Link>
        ))}
      </div>
    </div>
  );
}

export default BerichtenPagina;
