import React, { useState } from 'react';
import Webcam from 'react-webcam';
import './profiel-aanmaken.css';

const ProfielAanmaken = () => {
  const [formData, setFormData] = useState({
    email: '',
    nickname: '',
    oneline: '',
    waarderingRelatie: '',
    partnerZoeken: '',
    geslacht: '',
    interesseGeslacht: '',
    birthdate: '',
    postcode: '',
    relatieOpen: '',
    termsAccepted: false,
    facePhoto: null,
  });
  const [webcamEnabled, setWebcamEnabled] = useState(false);

  const handleChange = (e) => {
    const { name, value, type, checked } = e.target;
    setFormData({
      ...formData,
      [name]: type === 'checkbox' ? checked : value,
    });
  };

  const handleWebcamCapture = (webcamRef) => {
    const imageSrc = webcamRef.current.getScreenshot();
    setFormData({ ...formData, facePhoto: imageSrc });
    setWebcamEnabled(false);
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    // Voeg hier validatie en submit logica toe
    console.log(formData);
  };

  const webcamRef = React.useRef(null);

  return (
    <div className="profiel-aanmaken-container">
      <h2>Profiel Aanmaken</h2>
      <form onSubmit={handleSubmit}>
        <div className="form-group">
          <label htmlFor="email">E-mail:</label>
          <input
            type="email"
            id="email"
            name="email"
            value={formData.email}
            onChange={handleChange}
            required
          />
        </div>
        <div className="form-group">
          <label htmlFor="nickname">Nickname:</label>
          <input
            type="text"
            id="nickname"
            name="nickname"
            value={formData.nickname}
            onChange={handleChange}
            required
          />
        </div>
        <div className="form-group">
          <label htmlFor="oneline">Oneline:</label>
          <input
            type="text"
            id="oneline"
            name="oneline"
            value={formData.oneline}
            onChange={handleChange}
            required
          />
        </div>
        <div className="form-group">
          <label htmlFor="waarderingRelatie">Waardering Relatie:</label>
          <input
            type="text"
            id="waarderingRelatie"
            name="waarderingRelatie"
            value={formData.waarderingRelatie}
            onChange={handleChange}
            required
          />
        </div>
        <div className="form-group">
          <label htmlFor="partnerZoeken">Wat je zoekt in een partner:</label>
          <input
            type="text"
            id="partnerZoeken"
            name="partnerZoeken"
            value={formData.partnerZoeken}
            onChange={handleChange}
            required
          />
        </div>
        <div className="form-group">
          <label htmlFor="geslacht">Geslacht:</label>
          <select
            id="geslacht"
            name="geslacht"
            value={formData.geslacht}
            onChange={handleChange}
            required
          >
            <option value="">Selecteer</option>
            <option value="man">Man</option>
            <option value="vrouw">Vrouw</option>
            <option value="anders">Anders</option>
          </select>
        </div>
        <div className="form-group">
          <label htmlFor="interesseGeslacht">Welk geslacht interesseert je:</label>
          <select
            id="interesseGeslacht"
            name="interesseGeslacht"
            value={formData.interesseGeslacht}
            onChange={handleChange}
            required
          >
            <option value="">Selecteer</option>
            <option value="man">Man</option>
            <option value="vrouw">Vrouw</option>
            <option value="anders">Anders</option>
          </select>
        </div>
        <div className="form-group">
          <label htmlFor="birthdate">Geboortedatum:</label>
          <input
            type="date"
            id="birthdate"
            name="birthdate"
            value={formData.birthdate}
            onChange={handleChange}
            required
          />
        </div>
        <div className="form-group">
          <label htmlFor="postcode">Postcode:</label>
          <input
            type="text"
            id="postcode"
            name="postcode"
            value={formData.postcode}
            onChange={handleChange}
            required
          />
        </div>
        <div className="form-group">
          <label htmlFor="relatieOpen">Relatie waar je open voor staat:</label>
          <input
            type="text"
            id="relatieOpen"
            name="relatieOpen"
            value={formData.relatieOpen}
            onChange={handleChange}
            required
          />
        </div>
        <div className="form-group checkbox-group">
          <input
            type="checkbox"
            id="termsAccepted"
            name="termsAccepted"
            checked={formData.termsAccepted}
            onChange={handleChange}
            required
          />
          <label htmlFor="termsAccepted">
            Ik ga akkoord met de <a href="/terms">algemene voorwaarden</a> en <a href="/privacy">privacyregels</a>.
          </label>
        </div>
        <div className="form-group">
          <label>Gezichts foto:</label>
          {webcamEnabled ? (
            <div>
              <Webcam
                audio={false}
                ref={webcamRef}
                screenshotFormat="image/jpeg"
              />
              <button type="button" onClick={() => handleWebcamCapture(webcamRef)}>Capture Photo</button>
            </div>
          ) : (
            <button type="button" onClick={() => setWebcamEnabled(true)}>Enable Webcam</button>
          )}
          {formData.facePhoto && <img src={formData.facePhoto} alt="Face" />}
        </div>
        <button type="submit">Profiel Aanmaken</button>
      </form>
    </div>
  );
};

export default ProfielAanmaken;
