// src/Berichten/berichten.js
import React, { useState, useEffect } from 'react';
import { Link, Outlet } from 'react-router-dom';
import { messages } from './messages'; // Updated import

function BerichtenPagina() {
  const [messagesData, setMessagesData] = useState([]);

  // UseEffect to load data from messages.js
  useEffect(() => {
    setMessagesData(messages);  // Set messages state with data from the JSON file
  }, []);

  // Get the last message for each chat
  const lastMessages = messagesData.reduce((acc, msg) => {
    if (msg.sender === 'You') {
      return acc; // Skip your own messages for the contact list
    }

    if (!acc[msg.receiver]) {
      acc[msg.receiver] = msg; // Store the first message for a new receiver
    } else {
      // Update the message if it’s newer
      if (new Date(msg.timestamp) > new Date(acc[msg.receiver].timestamp)) {
        acc[msg.receiver] = msg;
      }
    }
    return acc;
  }, {});

  const lastMessagesArray = Object.values(lastMessages);

  return (
    <div style={{ display: 'flex' }}>
      {/* Left Sidebar: Chat List */}
      <div style={{ width: '300px', padding: '20px', borderRight: '1px solid #ddd' }}>
        <h2>Chats</h2>
        {lastMessagesArray.map((msg) => (
          <Link
            key={msg.id}
            to={`/berichten/${msg.id}`}
            style={{
              textDecoration: 'none',
              color: 'black',
              display: 'flex',
              marginBottom: '10px',
            }}
          >
            <img
              src={msg.profilePicture}
              alt={`${msg.sender}'s profile`}
              style={{ width: '50px', height: '50px', borderRadius: '50%' }}
            />
            <div style={{ marginLeft: '10px' }}>
              <p>
                <strong>{msg.receiver}</strong> <small>{msg.timestamp}</small>
              </p>
              <p>{msg.message}</p>
            </div>
          </Link>
        ))}
      </div>

      {/* Right Section: Displaying selected chat */}
      <div style={{ flex: 1, padding: '20px' }}>
        <h2>Chat Details</h2>
        <Outlet />
      </div>
    </div>
  );
}

export default BerichtenPagina;
