// src/Berichten/berichten.js
import React, { useState, useEffect } from 'react';
import { Link, Outlet } from 'react-router-dom'; // Import Outlet from react-router-dom
import { messages } from './messages'; // Import updated messages

function BerichtenPagina() {
  const [messagesData, setMessagesData] = useState([]);

  useEffect(() => {
    setMessagesData(messages); // Set messages state with data from the JSON file
  }, []);

  // Get the last message for each contact (excluding yourself)
  const lastMessages = messagesData.reduce((acc, msg) => {
    if (msg.sender === 'You') {
      return acc; // Skip your own messages from the contact list
    }

    const contactId = msg.receiver === 'You' ? msg.sender : msg.receiver; // Determine the contact ID

    if (!acc[contactId]) {
      acc[contactId] = msg; // Store the first message from the contact
    } else {
      // Update the stored message if the new message is later (based on timestamp)
      if (new Date(msg.timestamp) > new Date(acc[contactId].timestamp)) {
        acc[contactId] = msg;
      }
    }
    return acc;
  }, {});

  const lastMessagesArray = Object.values(lastMessages);

  return (
    <div style={{ display: 'flex', height: '100vh' }}>
      {/* Left Sidebar: Chat List */}
      <div style={{ width: '300px', padding: '20px', borderRight: '1px solid #ddd', overflowY: 'auto' }}>
        <h2>Chats</h2>
        {lastMessagesArray.map((msg) => (
          <Link
            key={msg.id}
            to={`/berichten/${msg.receiver === 'You' ? msg.sender : msg.receiver}`} // Fixed path for contact
            style={{
              textDecoration: 'none',
              color: 'black',
              display: 'flex',
              marginBottom: '10px',
            }}
          >
            <img
              src={msg.profilePicture}
              alt={`${msg.receiver === 'You' ? msg.sender : msg.receiver}'s profile`} // Fixed alt for image
              style={{ width: '50px', height: '50px', borderRadius: '50%' }}
            />
            <div style={{ marginLeft: '10px' }}>
              <p>
                <strong>{msg.receiver === 'You' ? msg.sender : msg.receiver}</strong>
                <small>{msg.timestamp}</small>
              </p>
              <p>{msg.message}</p>
            </div>
          </Link>
        ))}
      </div>

      {/* Right Section: Displaying selected chat */}
      <div style={{ flex: 1, padding: '20px', overflowY: 'auto' }}>
        <h2>Chat Details</h2>
        <Outlet /> {/* This is where the child routes (individual chat) will be rendered */}
      </div>
    </div>
  );
}

export default BerichtenPagina;
