// src/Berichten/ChatPage.js
import React from "react";
import { useParams } from "react-router-dom";

function ChatPage() {
  const { messageId } = useParams(); // Retrieve the message ID from the URL

  // Fake data for messages (same as before)
  const messages = [
    {
      id: 1,
      name: "Alice",
      profilePicture: "https://via.placeholder.com/50",
      message: "Hey! How are you doing?",
      timestamp: "2025-01-21T10:30:00",
    },
    {
      id: 2,
      name: "Bob",
      profilePicture: "https://via.placeholder.com/50",
      message: "I'm doing great, thanks! How about you?",
      timestamp: "2025-01-21T10:32:00",
    },
    {
      id: 3,
      name: "Charlie",
      profilePicture: "https://via.placeholder.com/50",
      message: "Don't forget about the meeting later.",
      timestamp: "2025-01-21T10:35:00",
    },
    {
      id: 4,
      name: "Alice",
      profilePicture: "https://via.placeholder.com/50",
      message: "Thanks for the reminder! See you there.",
      timestamp: "2025-01-21T10:36:00",
    },
    {
      id: 5,
      name: "Bob",
      profilePicture: "https://via.placeholder.com/50",
      message: "Let’s grab lunch after the meeting?",
      timestamp: "2025-01-21T10:37:00",
    },
  ];

  const message = messages.find((msg) => msg.id === parseInt(messageId));

  if (!message) {
    return <div>Message not found!</div>;
  }

  return (
    <div>
      <h2>Chat with {message.name}</h2>
      <div style={{ display: "flex", marginBottom: "10px" }}>
        <img
          src={message.profilePicture}
          alt={`${message.name}'s profile`}
          style={{ width: "50px", height: "50px", borderRadius: "50%" }}
        />
        <div style={{ marginLeft: "10px" }}>
          <p>
            <strong>{message.name}</strong> <small>{message.timestamp}</small>
          </p>
          <p>{message.message}</p>
        </div>
      </div>
    </div>
  );
}

export default ChatPage;
