import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import axios from 'axios';
import './login.css';

const Login = () => {
    const [email, setEmail] = useState('');
    const [password, setPassword] = useState('');
    const [error, setError] = useState('');
    const navigate = useNavigate(); // Hook voor navigatie

    const handleSubmit = async (e) => {
        e.preventDefault();

        try {
            const response = await axios.post('http://localhost:3001/users/login', { // Vervang door de URL van je backend API
                email,
                password
            }, {
                headers: {
                    'Authorization': `Bearer s4rTAy9VEa4/Uj1Hzqa02d45rf`, // Voeg je API-sleutel toe aan de headers
                }
            });

            console.log('Response status:', response.status);

            if (response.status === 200) {
                console.log('Succesvol ingelogd', response.data);
                navigate('/landpage'); // Stuur gebruiker door naar Landpage.js
            } else {
                console.log('Ongeldige inloggegevens:', response.data);
                alert('E-mail of wachtwoord is onjuist. Probeer het opnieuw.');
            }
        } catch (error) {
            console.error('Error during login:', error);
            setError('Login mislukt. Controleer je inloggegevens en probeer het opnieuw.');
        }
    };

    return (
        <form onSubmit={handleSubmit}>
            <input
                type="email"
                value={email}
                onChange={(e) => setEmail(e.target.value)}
                placeholder="Email"
                required
            />
            <input
                type="password"
                value={password}
                onChange={(e) => setPassword(e.target.value)}
                placeholder="Wachtwoord"
                required
            />
            <button type="submit">Inloggen</button>
            {error && <p>{error}</p>}
        </form>
    );
};

export default Login;
