// src/Berichten/ChatPage.js
import React from 'react';
import { useParams } from 'react-router-dom';
import messagesData from './messages'; // Import your messages data here

function ChatPage() {
  const { messageId } = useParams(); // Retrieve the message ID from the URL

  // Filter messages to show only the ones sent by 'You'
  const myMessages = messagesData.filter((msg) => msg.sender === 'You');

  // Find the specific message by ID
  const message = myMessages.find((msg) => msg.id === parseInt(messageId));

  if (!message) {
    return <div>Message not found!</div>;
  }

  return (
    <div>
      <h2>Chat with {message.receiver}</h2>
      <div style={{ display: 'flex', marginBottom: '10px' }}>
        <img
          src={message.profilePicture}
          alt={`${message.receiver}'s profile`}
          style={{ width: '50px', height: '50px', borderRadius: '50%' }}
        />
        <div style={{ marginLeft: '10px' }}>
          <p>
            <strong>{message.receiver}</strong> <small>{message.timestamp}</small>
          </p>
          <p>{message.message}</p>
        </div>
      </div>
    </div>
  );
}

export default ChatPage;
