// src/Berichten/ChatPage.js
import React from 'react';
import { useParams } from 'react-router-dom';
import { messages } from './messages'; // Import messages data

function ChatPage() {
  const { contactId } = useParams(); // Get the contact ID from the URL

  // Filter messages for the selected contact (excluding your own messages)
  const contactMessages = messages.filter(
    (msg) => msg.sender === contactId || msg.receiver === contactId
  );

  if (contactMessages.length === 0) {
    return <div>No messages found for this contact.</div>;
  }

  return (
    <div>
      <h2>Chat with {contactId}</h2>
      {contactMessages.map((msg) => (
        <div key={msg.id} style={{ display: 'flex', marginBottom: '10px' }}>
          <img
            src={msg.profilePicture}
            alt={`${msg.sender}'s profile`}
            style={{ width: '50px', height: '50px', borderRadius: '50%' }}
          />
          <div style={{ marginLeft: '10px' }}>
            <p>
              <strong>{msg.sender}</strong> <small>{msg.timestamp}</small>
            </p>
            <p>{msg.message}</p>
          </div>
        </div>
      ))}
    </div>
  );
}

export default ChatPage;
