import React, { useState } from 'react';
import { useParams } from 'react-router-dom';
import { messages as initialMessages } from './messages'; 

function ChatPage() {
  const { messageId } = useParams(); // Retrieve the messageId from the URL
  const [messages, setMessages] = useState(initialMessages); // Use state for messages
  const [newMessage, setNewMessage] = useState(''); // State for the new message input

  // Filter messages for the current contact
  const contactMessages = messages.filter(
    (msg) =>
      (msg.sender === messageId && msg.receiver === 'You') ||
      (msg.receiver === messageId && msg.sender === 'You')
  );

  // Sort messages by timestamp
  const sortedMessages = contactMessages.sort((a, b) => new Date(a.timestamp) - new Date(b.timestamp));

  // Handle sending a new message
  const handleSendMessage = () => {
    if (newMessage.trim() === '') return; // Prevent empty messages

    // Create a new message object
    const nieuwBericht = {
      id: messages.length + 1, // Generate a new ID (can be replaced later with backend ID)
      sender: 'You',
      receiver: messageId,
      message: newMessage,
      timestamp: new Date().toISOString(), // Current timestamp
      profilePicture: '/path-to-your-profile-picture.jpg', // Replace with an actual path or logic
    };

    // Update the state to include the new message
    setMessages((prevMessages) => [...prevMessages, newMessageObject]);
    setNewMessage(''); // Clear the input field
  };

  return (
    <div>
      <h2>Chat with {messageId}</h2>
      <div>
        {sortedMessages.map((msg) => (
          <div
            key={msg.id}
            style={{
              display: 'flex',
              marginBottom: '10px',
              justifyContent: msg.sender === 'You' ? 'flex-end' : 'flex-start',
            }}
          >
            <img
              src={msg.profilePicture}
              alt={`${msg.sender}'s profile`}
              style={{
                width: '50px',
                height: '50px',
                borderRadius: '50%',
                marginLeft: msg.sender === 'You' ? '0' : '10px',
                marginRight: msg.sender === 'You' ? '10px' : '0',
              }}
            />
            <div
              style={{
                backgroundColor: msg.sender === 'You' ? '#007bff' : '#f1f1f1',
                color: msg.sender === 'You' ? 'white' : 'black',
                padding: '10px',
                borderRadius: '10px',
                maxWidth: '70%',
                wordWrap: 'break-word',
              }}
            >
              <p>{msg.message}</p>
              <small>{new Date(msg.timestamp).toLocaleString()}</small>
            </div>
          </div>
        ))}
      </div>

      <div style={{ marginTop: '20px' }}>
        <input
          type="text"
          value={nieuwBericht}
          onChange={(e) => setNewMessage(e.target.value)}
          placeholder="Type your message"
          style={{ width: '80%', padding: '10px', marginRight: '10px' }}
        />
        <button onClick={handleSendMessage} style={{ padding: '10px 20px', cursor: 'pointer' }}>
          Send
        </button>
      </div>
    </div>
  );
  
}

export default ChatPage;
