import React, { useState } from 'react';
import { useParams } from 'react-router-dom';
import { messages as initialMessages } from './messages';
import { Container, Row, Col, Input, Button, Card, CardBody } from 'reactstrap';

function ChatPage() {
  const { messageId } = useParams();
  const [messages, setMessages] = useState(initialMessages);
  const [newMessage, setNewMessage] = useState('');

  const contactMessages = messages.filter(
    (msg) =>
      (msg.sender === messageId && msg.receiver === 'You') ||
      (msg.receiver === messageId && msg.sender === 'You')
  );

  const sortedMessages = contactMessages.sort((a, b) => new Date(a.timestamp) - new Date(b.timestamp));

  const handleSendMessage = () => {
    if (newMessage.trim() === '') return;

    const nieuwBericht = {
      id: messages.length + 1,
      sender: 'You',
      receiver: messageId,
      message: newMessage,
      timestamp: new Date().toISOString(),
      profilePicture: '/path-to-your-profile-picture.jpg',
    };

    setMessages((prevMessages) => [...prevMessages, nieuwBericht]);
    setNewMessage('');
  };

  return (
    <Container fluid className="h-100 d-flex flex-column">
      <Row className="mb-3">
        <Col>
          <h2 className="text-center">Chat with {messageId}</h2>
        </Col>
      </Row>
      <Row className="flex-grow-1 overflow-auto">
        <Col>
          {sortedMessages.map((msg) => (
            <div
              key={msg.id}
              className={`d-flex mb-3 ${msg.sender === 'You' ? 'justify-content-end' : 'justify-content-start'}`}
            >
              <img
                src={msg.profilePicture}
                alt={`${msg.sender}'s profile`}
                className="rounded-circle"
                style={{ width: '50px', height: '50px', margin: '0 10px' }}
              />
              <Card className={`p-2 ${msg.sender === 'You' ? 'bg-primary text-white' : 'bg-light'}`}>
                <CardBody>
                  <p>{msg.message}</p>
                  <small className="text-muted">{new Date(msg.timestamp).toLocaleString()}</small>
                </CardBody>
              </Card>
            </div>
          ))}
        </Col>
      </Row>
      <Row>
        <Col>
          <div className="d-flex">
            <Input
              type="text"
              value={newMessage}
              onChange={(e) => setNewMessage(e.target.value)}
              placeholder="Type your message"
              className="me-2"
            />
            <Button color="primary" onClick={handleSendMessage}>
              Send
            </Button>
          </div>
        </Col>
      </Row>
    </Container>
  );
}

export default ChatPage;
