// src/Berichten/berichten.js
import React, { useState, useEffect } from 'react';
import { Link, Outlet } from 'react-router-dom';
import { messages } from '../messages';  // Import messages from the new file

function BerichtenPagina() {
  const [messageList, setMessageList] = useState([]);

  // Set messages state with data from messages.js
  useEffect(() => {
    setMessageList(messages);
  }, []);

  return (
    <div style={{ display: 'flex' }}>
      {/* Left Sidebar: Chat List */}
      <div style={{ width: '300px', padding: '20px', borderRight: '1px solid #ddd' }}>
        <h2>Chats</h2>
        {messageList.map((msg) => (
          <Link
            key={msg.id}
            to={`/berichten/${msg.id}`}
            style={{
              textDecoration: 'none',
              color: 'black',
              display: 'flex',
              marginBottom: '10px',
            }}
          >
            <img
              src={msg.profilePicture}
              alt={`${msg.sender}'s profile`}
              style={{ width: '50px', height: '50px', borderRadius: '50%' }}
            />
            <div style={{ marginLeft: '10px' }}>
              <p>
                <strong>{msg.sender}</strong> <small>{msg.timestamp}</small>
              </p>
              <p>{msg.message}</p>
            </div>
          </Link>
        ))}
      </div>

      {/* Right Section: Displaying selected chat */}
      <div style={{ flex: 1, padding: '20px' }}>
        <h2>Chat Details</h2>
        <Outlet />
      </div>
    </div>
  );
}

export default BerichtenPagina;
